import { useState, useEffect } from 'react';


export default function useAuthListener() {

    const { user, isAuthenticated, error, loading } = useSelector(state => state.auth)
    const [activeUser, setActiveUser] = useState(null)

    useEffect(() => {
        if (user) {
            setActiveUser(user)
        } else {
            setActiveUser(null)
        }
    }, [user])
    return { user }
}
